    private void jBTSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTSalvarActionPerformed
        if (!jTFPlaca.getText().equals("")) {
            Veiculo veiculo = new Veiculo();
            if (veiculoId != -1) {
                veiculo.setVeiculoId(veiculoId);
            }
            veiculo.setPlaca(jTFPlaca.getText());
            veiculo.setModelo(jTFModelo.getText());
            veiculo.setMarca(jTFMarca.getText());
            veiculo.setLugares(jTFLugares.getText());
            veiculo.setValorAluguel(jTFValorAluguel.getText());

            VeiculoDAO veiculodao = new VeiculoDAOMySQL();
            if (veiculodao.salvar(veiculo)) {
                javax.swing.JOptionPane.showMessageDialog(null, "Ve√≠culo cadastrado com suscesso!");
            } else {
                javax.swing.JOptionPane.showMessageDialog(null, "Ve√≠culo n√£o cadastrado!");
            }
        } else {
            javax.swing.JOptionPane.showMessageDialog(null, "O Nome deve ser preenchido!");
            jTFPlaca.requestFocus();
        }
    }//GEN-LAST:event_jBTSalvarActionPerformed

