    private void jBTExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBTExcluirActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser exclu√≠do");
        VeiculoDAO veiculodao = new VeiculoDAOMySQL();
        Veiculo veiculo = veiculodao.consultarPK(id);
        if (veiculo != null) {
            jTFPlaca.setText(veiculo.getPlaca());
            jTFModelo.setText(veiculo.getModelo());
            jTFMarca.setText(veiculo.getMarca());
            jTFLugares.setText(veiculo.getLugares());
            jTFValorAluguel.setText(veiculo.getValorAluguel());
            Object[] opcoes = {"Sim", "N√£o"};
            int resposta = javax.swing.JOptionPane.showOptionDialog(null, "Confirma a exclus√£o do Ve√≠culo?", "Exclus√£o", 0, javax.swing.JOptionPane.QUESTION_MESSAGE, null, opcoes, opcoes[1]);
            if (resposta == 0) {
                if (veiculodao.apagarPK(Integer.parseInt(id))) {
                    javax.swing.JOptionPane.showMessageDialog(null, "Ve√≠culo exclu√≠do com suscesso!");
                    jTFMarca.setText("");
                    jTFModelo.setText("");
                    jTFLugares.setText("");
                    jTFValorAluguel.setText("");
                    jTFPlaca.setText("");
                } else {
                    javax.swing.JOptionPane.showMessageDialog(null, "Ve√≠culo n√£o exclu√≠do!");
                }
            }
        }
    }//GEN-LAST:event_jBTExcluirActionPerformed

    private void JBTAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBTAlterarActionPerformed
        String id = javax.swing.JOptionPane.showInputDialog("Digite o id a ser alterado");
        VeiculoDAO veiculodao = new VeiculoDAOMySQL();
        Veiculo veiculo = veiculodao.consultarPK(id);
        if (veiculo != null) {
            veiculoId = veiculo.getVeiculoId();
            jTFPlaca.setText(veiculo.getPlaca());
            jTFModelo.setText(veiculo.getModelo());
            jTFMarca.setText(veiculo.getMarca());
            jTFLugares.setText(veiculo.getLugares());
            jTFValorAluguel.setText(veiculo.getValorAluguel());
        }
    }//GEN-LAST:event_JBTAlterarActionPerformed

    private void JBTReajustarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBTReajustarActionPerformed
        double percentual = Double.parseDouble(javax.swing.JOptionPane.showInputDialog("Digite o percentual de reajuste do \nvalor do aluguel dos ve√≠culos"));
        VeiculoDAO veiculodao = new VeiculoDAOMySQL();
        int resultado = veiculodao.reajustar(percentual);
        if (resultado != 0) {
            javax.swing.JOptionPane.showMessageDialog(null, "Foram reajustados o valor do aluguel de " + resultado + " Ve√≠culo(s)!");
        } else {
            javax.swing.JOptionPane.showMessageDialog(null, "Nenhum Ve√≠culo foi reajustado!");
        }
    }//GEN-LAST:event_JBTReajustarActionPerformed

